package domain;import java.math.BigDecimal;import java.util.GregorianCalendar;public abstract class Card implements CardInterface {    protected BigDecimal balance;    protected String ownerName;    protected int cardNumber;    protected GregorianCalendar expirationDate;    public Card(BigDecimal balance, String ownerName, int cardNumber, GregorianCalendar expirationDate) {        this.balance = balance;        this.ownerName = ownerName;        this.cardNumber = cardNumber;        this.expirationDate = expirationDate;    }    public BigDecimal getBalance() {        return balance;    }    public void setBalance(BigDecimal balance) {        this.balance = balance;    }    public String getOwnerName() {        return ownerName;    }    public void setOwnerName(String ownerName) {        this.ownerName = ownerName;    }    public int getCardNumber() {        return cardNumber;    }    public void setCardNumber(int cardNumber) {        this.cardNumber = cardNumber;    }    public GregorianCalendar getExpirationDate() {        return expirationDate;    }    public void setExpirationDate(GregorianCalendar expirationDate) {        this.expirationDate = expirationDate;    }    public void deposit(BigDecimal amount) {        balance = balance.add(amount);    }}