import io.dropwizard.Application;import io.dropwizard.setup.Environment;import org.glassfish.jersey.logging.LoggingFeature;import java.util.logging.Logger;import resource.AtmResourceV1;import sun.tools.java.Environment;public class KatesAtmApplication extends Application<KatesAtmApplicationConfig> {    public static void main(String[] args) throws Exception {        new KatesAtmApplication().run(args);    }    @Override    public void run(KatesAtmApplicationConfig config, Environment environment) throws Exception {        environment.jersey().register(new RuntimeExceptionMapper());        environment.jersey().register(new WebapplicationExceptionmapper());        environment.jersey().register(new AtmResourceV1());        environment.jersey().register(new LoggingFeature(Logger.getLogger("se.bank"), LoggingFeature.Verbosity.PAYLOAD_ANY));    }}