package domain;import javax.smartcardio.Card;import java.math.BigDecimal;import java.util.GregorianCalendar;public class CreditCard extends Card {    public static final BigDecimal CREDIT_FEE = BigDecimal.valueOf(25);    public CreditCard(BigDecimal balance, String ownerName, int cardNumber, GregorianCalendar expirationDate) {    super(balance, ownerName, cardNumber, expirationDate);    }    @Override    public void withdraw(BigDecimal amount) {        balance = balance.subtract(amount.add(CREDIT_FEE));    }}