package domain;import org.junit.Test;import java.math.BigDecimal;import static org.junit.Assert.assertTrue;public class CreditCardTest extends ATMCardBaseTest {    public CreditCardTest() {        super (new CreditCard(DEFAULT_BALANCE, DEFAULT_OWNER_NAME, DEFAULT_CARD_NUMBER, DEFAULT_EXPIRATION_DATE));    }    @Test    @Override    public void withdraw() throws Exception {        BigDecimal withdrawAmount = BigDecimal.TEN;        atmCard.withdraw(withdrawAmount);        assertTrue(atmCard.getBalance().equals(DEFAULT_BALANCE.subtract(withdrawAmount.add(CreditCard.CREDIT_FEE))));    }}